CREATE FUNCTION [dbo].[GetCommaSeperatedData] (@Data VARCHAR(400)) RETURNS VARCHAR(400) AS BEGIN DECLARE @ReturnedData VARCHAR(400) SET @ReturnedData = '''' SELECT @ReturnedData = COALESCE(@ReturnedData+'','' ,'''') + ('''''''' + LTRIM(RTRIM(Individual)) + '''''''') FROM  dbo.SplitToVarCharWithIndex(@Data) WHERE LTRIM(RTRIM(Individual)) <> '''' SET @ReturnedData = SUBSTRING(@ReturnedData,2, LEN(@ReturnedData)) RETURN 	@ReturnedData END
